/**
 * Allow importing static assets as modules.
 * Bun/Vite resolves these to file paths.
 * For WASM you're better of
 */

declare module "*.png"
declare module "*.gif"
declare module "*.webp"
declare module "*.avif"
declare module "*.ico"
declare module "*.bmp"
declare module "*.tiff"
declare module "*.svg"
declare module "*.jpeg"
declare module "*.jpg"

declare module "*.woff"
declare module "*.woff2"
declare module "*.ttf"
declare module "*.otf"
declare module "*.eot"

declare module "*.mp4"
declare module "*.webm"
declare module "*.ogg"
declare module "*.mov"
declare module "*.mp3"
declare module "*.wav"
declare module "*.flac"
declare module "*.aac"

declare module "*.pdf"
declare module "*.xml"
declare module "*.csv"
declare module "*.txt"
declare module "*.md"

declare module "*.css"
